INSERT INTO `witnesses` VALUES ('27', '七姑娘工作室', '中山市三乡镇雅居樂新城都汇城G3-205', '房地产', '0', '其他举报', '許生', ' 微信 180221833409', '18022183409', '……', '住宅楼房开設商铺', null, '2017年', '在住宅楼房开設商铺，在门口设置商店招牌，在阳台设置广告灯箱，遗反住宅楼宇法規，难度你们的管埋人员看不到，为何沒跟進?', '同上', null, '/datas/file/86d6d058c43fb28ee4cc9df17e1b378e.jpg', null, null, '2017-05-27 07:57:22');
INSERT INTO `witnesses` VALUES ('28', '七姑娘工作室', '中山市三乡镇雅居樂新城都汇城G3-205', '物业公司', '0', '其他举报', '許先生', 'Wechat 18022183409', '18022183409', '---', '住宅开设商铺', null, '2017', '他们的客人出入，所耗的消费，如升降机电费，谁來承担，最主要陌生人出入多了，影响治安，你们是上市公司，入伙時合约规定只供住宅用途，况且该商铺已违反國家:法規，你们要正视和盡快解決，以不损贵司声譽。', '已说明', null, '/datas/file/6f4fea83aa97e7294b2a0d379a340ef2.jpg', null, null, '2017-05-27 09:55:03');
