INSERT INTO `notice` VALUES ('175', '1000000184', 'agile cambridgeshire kindergarten', '', '2017', '', '1', '勾王鹏', '', null, '<p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868010626948.jpg\" style=\"\" title=\"1483868010626948.jpg\"/></p><p><br/></p><p style=\"text-indent:31px;line-height:37px\"><span style=\"font-family: 黑体,SimHei; font-size: 16px;\">雅居乐剑桥郡国际幼儿园是雅居乐地产置业有限公司为服务雅居乐业主及满足适龄孩子入学所创建的一所高品质、国际化的一所全日制民办幼儿园。总投资2000万元,创建于2010年9月，于2011年9月正式开园。幼儿园占地面积5400平方米，建筑面积4320平方米，园林及户外活动场地3840平米；办园规模达到16个班级,幼儿人数达460人.园内设大型多功能厅、综合音体室、感觉训练统合室、图书阅览室、陶泥室、美术室、科技启蒙室、综合游戏室、情景游戏区、轮滑区、家长接待厅等；户外场地分别有运动软地约2500平方米、大型户外游乐设施、沙池区、游泳池、戏水池、跑道、水生动物观赏区、种植园地、动物养殖园、攀岩墙、读书角等；为幼儿的成长提供了最优质的软硬件条件。寓教学、启智、保育、游戏为一体，是一所高品质的实验示范性幼儿园。2012年被评为番禺区一级幼儿园.</span></p><p style=\"text-indent:31px;line-height:37px\"><span style=\"font-size: 16px; font-family: 黑体,SimHei;\">目前，我园办园规模目前16个班级，幼儿总人数为465人，我园拥有一支高素质的师资队伍，教职员工共62人；其中，园长1名、副园长1名，主任1名，专职教师36名，保育员16名，保健医生2名、财务人员2名，后勤人员3名。外籍教师均具备专业教育资格证书。教师具有学前教育专业专科以上学历，经全方位的培训聘用上岗。园长均本科以上学历，拥有园长上岗证。每班配两教一保，教师的学历达标率均为100%。</span></p><p style=\"text-indent:31px;line-height:37px\"><span style=\"font-size: 16px; font-family: 黑体,SimHei;\">办园以来，幼儿园在区政府、区教育局幼教科、镇指导中心等有关部门的正确指导下，认真贯彻以《幼儿园工作规程》、《幼儿园教育指导纲要（试行）》的精神，坚持正确的教育方针，树立科学办园理念，依法治园，培养幼儿健康、快乐、全面发展。幼儿园勇于创新，积极进取，不断提高保教质量和服务品质，努力打造园本办园特色，取得良好效果，受到家长和社会的好评。并于2013年被评为“番禺区好园所”的称号。</span></p><p style=\"text-indent:31px;line-height:37px\"><span style=\"font-size: 16px; font-family: 黑体,SimHei;\"><img src=\"/datas/ueditor/php/upload/image/20170108/1483868164755950.jpg\" title=\"1483868164755950.jpg\" alt=\"4.1雅居乐剑桥郡幼儿园.jpg\"/><br/></span></p><p style=\"text-indent:31px;line-height:37px\"><br/><span style=\"font-size: 16px; font-family: 黑体,SimHei;\"></span></p><p style=\"text-indent:31px;line-height:37px\"><span style=\"font-size: 16px; font-family: 宋体;\">&nbsp;<span style=\"font-size: 16px; font-family: 黑体,SimHei;\">剑桥郡幼儿园将致力为孩子们营造开心、自由、健康的成长环境，实现教育与国际接轨，保教并重，双语教学特色，奥尔夫音乐艺术熏陶教育。剑桥郡幼儿园将打造成雅居乐精品示范性幼儿园。 </span></span></p><p style=\"line-height:37px\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px; font-family: 华文中宋;\">★</span><span style=\"font-size: 16px; font-family: 黑体,SimHei;\">办园理念</span></span></p><p style=\"text-indent:31px;line-height:37px\"><span style=\"font-family: 黑体,SimHei; font-size: 16px;\">&nbsp; 致力培养多才多艺，身心全面发展的孩子。</span></p><p style=\"line-height:37px\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px; font-family: 华文中宋;\">★</span><span style=\"font-size: 16px; font-family: 黑体,SimHei;\">办园宗旨</span></span></p><p style=\"margin-left:27px;line-height:37px\"><span style=\"font-family: 黑体,SimHei; font-size: 16px;\">&nbsp; 遵循着“一切为了孩子、为了孩子的一切、为了一切的孩子”的宗旨竭诚为广大孩子、家长服务。</span></p><p style=\"line-height:37px\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px; font-family: 华文中宋;\">★</span><span style=\"font-size: 16px; font-family: 黑体,SimHei;\">教育模式</span></span></p><p style=\"margin-left:28px;line-height:37px\"><span style=\"font-family: 黑体,SimHei; font-size: 16px;\">&nbsp; 吸纳了我国博大精深的文化精髓，同时与西方开放式、个性化的教育理念有机结合、秉承最适合中国孩子成长的多元化国际教育模式，并引进一流的互动式电教设备，让孩子们在“玩中学、学中玩”，致力于培养多才多艺、身心健康、独具创造力的孩子。</span></p><p style=\"line-height:37px\"><span style=\"font-size: 16px; font-family: 黑体,SimHei;\">★课程体系—特色课程</span></p><p style=\"text-indent:16px;line-height:37px\"><span style=\"font-family: 黑体,SimHei; font-size: 16px;\">基础课程—幼儿创意学习课程</span></p><p style=\"text-indent:16px;line-height:37px\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px;\">英语课程</span><span style=\"font-size: 16px;\">—SK</span><span style=\"font-size: 16px;\">少儿皇家英语</span></span></p><p style=\"text-indent:16px;line-height:37px\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px;\">艺术课程</span><span style=\"font-size: 16px;\">—</span><span style=\"font-size: 16px;\">美育奥尔夫音乐</span></span></p><p style=\"text-indent: 16px; line-height: 37px;\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px;\">阅读课程</span><span style=\"font-size: 16px;\">—</span><span style=\"font-size: 16px;\">互动分享阅读</span></span></p><p style=\"text-indent: 16px; line-height: 37px;\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px;\"></span></span></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868194202718.jpg\" style=\"\" title=\"1483868194202718.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868195541235.jpg\" style=\"\" title=\"1483868195541235.jpg\"/></p>', '/datas/image/source/fdb2fda0dfb4b80a067bf01a0d9705b8.jpg', '/datas/image/thumbnail/fdb2fda0dfb4b80a067bf01a0d9705b8.thb', null, '/datas/image/source/4d902eebe95d4682fb2d9d797e3eafee.jpg', '/datas/image/thumbnail/4d902eebe95d4682fb2d9d797e3eafee.thb', null, null, null, null, '1', '259', '1', '10005', '2016-12-12 11:42:04', '2017-02-04 14:44:46', '2016-12-12', null, '0', '0', '0');
INSERT INTO `notice` VALUES ('176', '1000000184', 'agile yuhu kindergarten', '', '2017', '', '2', '勾王鹏', '', null, '<p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868662540389.jpg\" title=\"1483868662540389.jpg\" alt=\"6.雅居乐玉湖幼儿园.jpg\"/></p><p><br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-family: 黑体,SimHei;\"> 雅居乐玉湖幼儿园成立于二0一三年一月，地处番禺区雅居乐花园小区内，是一所由番禺雅居乐房地产有限公司投资，由广东雅居乐教育投资管理有限公司承办的“全日制”普惠性幼儿园。幼儿园园舍独立，布局合理，设施齐备，资金稳定，师资优良，管理规范。</span></p><p><span style=\"font-family: 黑体,SimHei;\"><br/></span></p><p><span style=\"font-family: 黑体,SimHei;\">&nbsp;&nbsp;&nbsp; 园舍占地面积5100M2，生均占地面积14M2，建筑面积3570M2，生均建筑面积10M2,户外活动场地面积3032.4M2，生均户外活动场地面积8.5M2，绿化覆盖率为54%，我园环境优美，设备设施完善，合理布局各种用房，拥有园长办公室、行政办公室、财务办公室等三间办公室，规范合理的保健隔离室一间，宽敞优雅的幼儿教学活动课室16间，设计新颖美观的音体室、科学室、美术活动室、图书阅览室等四间功能室；户外场地由前、后两个操场构成：安全趣味的滑梯、体现童真的戏水池、沙池，综合的游戏区、种植区等设备设施构成。幼儿园资源得到了充分的整合，每个课室配备了笔记本电脑、电视、音响等先进的多媒体教学设备等完善的办学硬件满足了现代幼儿教育发展的需要。</span></p><p><br/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868700887950.jpg\" title=\"1483868700887950.jpg\" alt=\"yuhu-005.jpg\"/></p><p>&nbsp;<br/> </p><p><span style=\"font-family: 黑体,SimHei;\">&nbsp;&nbsp;&nbsp; 目前我园现有教职工73人，其中正园长1人、副园长1人，主任2人，教师 33人，保育员16人，保健医生4人，财务人员2人，后勤人员15人，其中，带班教师专业学历达标率为100%。目前，全园有16个班级（其中大班6个、中班5个、小班5个），每班配两教一保，现在园幼儿530 名。</span></p><p><span style=\"font-family: 黑体,SimHei;\"><br/></span></p><p style=\"line-height:37px\"><span style=\"font-family: 黑体,SimHei;\"><span style=\"font-size: 16px; font-family: 华文中宋;\">★<span style=\"font-size: 16px; font-family: 黑体,SimHei;\">特色课程</span></span><span style=\"font-size: 16px; font-family: 黑体,SimHei;\"></span></span></p><p><span style=\"font-family: 黑体,SimHei;\">&nbsp;&nbsp;&nbsp; 我园在促进幼儿全面发展的同时，尝试以趣味体能教育作为突破口，创办趣味体能特色。在确定办园方向后，我园邀请了育苗体能教育机构的男老师对幼儿园体能方面进行深入渗透，为创办自己的特色提供了有利的保证。并向教师们提出三个必须：一是必须掌握最基本乐理知识，熟悉掌握简谱和五线谱的唱谱法，了解简单的和声、曲式及有关作家、作品、乐器等方面知识。二是必须具有理解、欣赏音乐艺术的能力，能了解大量的中外名曲、幼儿音乐并具有指导幼儿欣赏音乐的能力。三是必须具备表演方面的素养，掌握正确的歌唱方法和弹奏方法，并能正确处理和表现歌曲、舞蹈等，具备创作、改编音乐作品、创编歌舞的能力。目前，幼儿园园级课题“幼儿园歌唱活动的探索与研究”进行初步阶段；“玉湖幼儿园小朋友歌曲集”已装订成册。</span></p><p><span style=\"font-family: 黑体,SimHei;\"><br/></span></p><p><span style=\"font-family: 黑体,SimHei;\"></span></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868839951552.jpg\" style=\"\" title=\"1483868839951552.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868841822364.jpg\" style=\"\" title=\"1483868841822364.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483868841452121.jpg\" style=\"\" title=\"1483868841452121.jpg\"/></p>', '/datas/image/source/d4c5decca3ce73026036f1b326b94888.jpg', '/datas/image/thumbnail/d4c5decca3ce73026036f1b326b94888.thb', null, '/datas/image/source/9ec86c0494e44ac71a02a17cb48ba257.jpg', '/datas/image/thumbnail/9ec86c0494e44ac71a02a17cb48ba257.thb', null, null, null, null, '1', '253', '1', '10005', '2016-12-12 11:42:39', '2017-02-04 14:44:53', '2016-12-12', null, '0', '0', '0');
INSERT INTO `notice` VALUES ('178', '1000000185', '中山纪中雅居乐凯茵学校', '', '', '', '0', '勾王鹏', null, null, '<p><span style=\"font-family: 黑体,SimHei;\"><img src=\"/datas/ueditor/php/upload/image/20170108/1483872838968647.jpg\" title=\"1483872838968647.jpg\" alt=\"jiya-001.jpg\"/></span></p><p><span style=\"font-family: 黑体,SimHei;\">&nbsp;</span></p><p><span style=\"font-family: 黑体,SimHei;\">&nbsp;&nbsp;&nbsp; 中山市纪中雅居乐凯茵学校是中山纪念中学携手雅居乐集团联合创办的全日制寄宿制高端民办学校，延续中山纪念中学培育高素质现代化人才的优质教育理念，秉持“中山精神+精英素养”的办学宗旨，融合纪中百年名校传统与雅居乐精英文化，以博爱做人、博学成才、博弈行事、博采创新为育人目标，致力培养具有开阔视野、包容胸怀、社会责任、人文精神、科学素养的精英后备人才。纪中雅居乐凯茵学校将充分发挥强强联合的优势，打造精英教育品牌，力求早日跻身全市知名、省内有影响的民办学校之列。</span></p><p><br/><span style=\"font-family: 黑体,SimHei;\"> 　　学校位于中山市长江旅游生态风景保护区内，校园总规划面积约6.2万平方米。这里的环境——群山环抱，山明水秀，幽静优雅，是理想读书之所；这里的师资——由纪中校本部以及纪中三鑫学校的宝贵管理方式和教学经验引领，通过“外引内培”会聚八方精英，是一支师德高尚、素质过硬的教育者队伍；这里的硬件——各类设施均依照国家示范性学校标准设计和配备，所毗邻的全国十佳36洞国际标准的雅居乐长江高尔夫球场亦为学校可利用的独特资源。这一切都为家长的放心托付、学校的前景发展提供了最为关键的保障。</span></p><p><span style=\"font-family: 黑体,SimHei;\">&nbsp;</span></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872859139016.jpg\" style=\"\" title=\"1483872859139016.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872859401219.jpg\" style=\"\" title=\"1483872859401219.jpg\"/></p><p><span style=\"font-family: 黑体,SimHei;\"></span><br/></p><p><span style=\"font-family: 黑体,SimHei;\">　　纪中雅居乐凯茵学校致力于学生的可持续发展、致力于高素质精英人才的培养，坚持“用传统优秀文化滋养，用现代精英素养启发”的教学思想。学校的课程安排具有以下五方面的特色：</span></p><p><br/><span style=\"font-family: 黑体,SimHei;\"> 　　其一，以“双语见长，多语可选”为特色。在普通中学设置的文化课之外，学校全方位提升中英“双语”教学质量，同时将会开设日语、韩语、法语等语言的选修课程。</span></p><p><br/><span style=\"font-family: 黑体,SimHei;\"> 　　其二，以“尊重学生，发展个性”为特色。为“内化学生综合素质，外扬学生个性特长”，学校将根据实际需要设置包括高尔夫课程在内的优质体育教学、包括多种乐器舞蹈可选的全面艺术教学、包括雅居乐经营管理的金融课程、包括品析各类演讲名家的口才课程等。</span></p><p><span style=\"font-family: 黑体,SimHei;\">&nbsp;</span></p><p><span style=\"font-family: 黑体,SimHei;\">　　其三，以“秉承传统，放眼现代”为特色。学校坚持用中山精神来鼓励学生的成长，坚持用优秀的传统文化滋养学生的内质，同时，通过培养学生“有专长、懂合作、善动手、勤探索”的能力，培育具有高度集体荣誉感、懂得高效团队合作、具备较强综合素质的现代领袖魅力型人才。</span></p><p><br/><span style=\"font-family: 黑体,SimHei;\"> 　　其四，以“积极沟通，国际接轨”为特色。学校将与香港等境外学校进行深入的交流和合作，引进他们先进的教学模式，并实地走进他们英语授课的课堂，届时将聘请多位国际知名的教育顾问，力争学校办学视野，既能立足本土实情，又能与国际接轨。</span></p><p><br/><span style=\"font-family: 黑体,SimHei;\"> 　　其五，以“家校共进、携手发展”为特色。学校将会定时定量为家长提供自我增值、与其他优秀家长沟通的课程与服务，保质保量提供与学生共学共进的亲子活动和机会等，为学生的成长提供温馨的氛围和高素质的空间。</span></p><p><br/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872886203254.jpg\" style=\"\" title=\"1483872886203254.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872886659120.jpg\" style=\"\" title=\"1483872886659120.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872886703616.jpg\" style=\"\" title=\"1483872886703616.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872886815149.jpg\" style=\"\" title=\"1483872886815149.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872887470757.jpg\" style=\"\" title=\"1483872887470757.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872887849861.jpg\" style=\"\" title=\"1483872887849861.jpg\"/></p><p><img src=\"/datas/ueditor/php/upload/image/20170108/1483872887491932.jpg\" style=\"\" title=\"1483872887491932.jpg\"/></p>', '/datas/image/source/1ff9398af898e6d9d4a4f2d17ce9b537.jpg', '/datas/image/thumbnail/1ff9398af898e6d9d4a4f2d17ce9b537.thb', null, '/datas/image/source/8deebbaa0a00d8ac1df39b24b69e99d7.jpg', '/datas/image/thumbnail/8deebbaa0a00d8ac1df39b24b69e99d7.thb', null, null, null, null, '0', '151', '1', '10005', '2016-12-12 11:43:54', '2017-01-08 18:55:09', '2016-12-12', null, '0', '0', '0');
